var React, div, polygon, svg;

React = require('react');

div = React.createFactory('div');

svg = React.createFactory('svg');

polygon = React.createFactory('polygon');

module.exports = React.createClass({
  displayName: 'triangle',
  propTypes: {
    type: React.PropTypes.string
  },
  render: function() {
    return div({
      className: 'svg-triangle'
    }, (function() {
      switch (this.props.type) {
        case 'right':
          return svg({
            width: '8',
            height: '8',
            viewBox: '0 0 8 8',
            fill: 'grey'
          }, polygon({
            points: '0 0 4 4 0 8'
          }));
        default:
          return svg({
            width: '8',
            height: '8',
            viewBox: '0 0 8 8',
            fill: 'grey'
          }, polygon({
            points: '0,0 8,0 4,4'
          }));
      }
    }).call(this));
  }
});
